import React from 'react';
import 'bootstrap/dist/css/bootstrap.min.css'
import './App.css';
import {createStore} from "./create-store";
import {rootReducer} from "./redux/rootReducer";
import {DECREMENT, INCREMENT} from "./constants";

const counter = document.getElementById('counter');
const addBtn = document.getElementById('add')
const subBtn = document.getElementById('sub')
const themeBtn = document.getElementById('theme')
const asyncBtn = document.getElementById('async')
const store = createStore(rootReducer,0);

function App() {
window.store.getState();
    return (
      <div className="container pt-5">
        <h1 className="heading">
          <span>Redux</span>
          <button className="btn btn-info" id="theme">Сменить тему</button>
        </h1>
        <hr/>
        <div className="card container">
          <div className="card-body">
            <h5 className="card-title">Счетчик: <span id="counter"></span></h5>
              <div className='row '>
            <button className="btn btn-primary mr-1" id="add">Добавить</button>
            <button className="btn btn-danger mr-1" id="sub">Убрать</button>
            <button className="btn btn-success " id="async">Async</button></div>
          </div>
        </div>
      </div>

);
    addBtn.addEventListener('click',()=>{
        store.dispatch({type:INCREMENT});

    })
    subBtn.addEventListener('click',()=>{
        store.dispatch({type:DECREMENT})
    })
    asyncBtn.addEventListener('click',()=>{})
    store.subscribe(()=>{
        const state = store.getState();
        counter.textContent = state;
    });
}



export default App;
