export function createStore(rootReducer) {
    let state = {}
    const subscrubers=[]
return {

    dispatch(action){
state = rootReducer(state,action)
    },
    subscribe(callback){
        subscrubers.push(callback);
    },
    getState(){
        return state;
    }
}
}
